<script>
import WisBase from '../includes/pin-mapper/modules/wisbase/index'
import WisCore from '../includes/pin-mapper/modules/wiscore/index'
import WisIo from '../includes/pin-mapper/modules/wisio/index'
import WisSensor from '../includes/pin-mapper/modules/wissensor/index'
import WisSensorB from '../includes/pin-mapper/modules/wissensor-b/index'
import WisSensorC from '../includes/pin-mapper/modules/wissensor-c/index'
import WisSensorD from '../includes/pin-mapper/modules/wissensor-d/index'
import Pins from '../includes/pin-mapper/modules/pin-mapping/index';

export default {
  name: 'RkPinMapper',
  data: ()=>(
    {
      wiscoreModels : [],
      wisbaseModels : [],
      wisioModels : [],
      wisSensorModelsA : [],
      wisSensorModelsB : [],
      wisSensorModelsC : [],
      wisSensorModelsD : [],
      wisIoBoard : [],
      wisSensorBoardA : [],
      wisSensorBoardB : [],
      wisSensorBoardC : [],
      wisSensorBoardD : [],
      currentWisBase : null,
      currentWisCore : null,
      currentWisIo : null,
      currentWisSensorA : null,
      currentWisSensorB : null,
      currentWisSensorC : null,
      currentWisSensorD : null,
      classStyles : []
    }
  ),
  computed : {
    wisBaseDropdowns(){

      WisBase.modules.forEach(item => {
        this.wisbaseModels.push(item.model);
      });
      this.currentWisBase = this.wisbaseModels[0]
      return this.wisbaseModels;
    },
    wisCoreDropdowns(){

      WisCore.modules.forEach(item => {
        this.wiscoreModels.push(item.model);
      });
      this.currentWisCore = this.wiscoreModels[0]
      return this.wiscoreModels;
    },
    wisIoDropdowns(){

      WisIo.modules.forEach(item => {
        this.wisioModels.push(item.model);
      });   
      this.currentWisIo = this.wisioModels[0]
      return this.wisioModels;
    },
    wisSensorDropdownsA(){

      WisSensor.modules.forEach(item => {
        this.wisSensorModelsA.push(item.model);
      });   
      this.currentWisSensorA = this.wisSensorModelsA[0]
      return this.wisSensorModelsA;
    },
    wisSensorDropdownsB(){

      WisSensor.modules.forEach(item => {
        this.wisSensorModelsB.push(item.model);
      });   
      this.currentWisSensorB = this.wisSensorModelsB[1]
      return this.wisSensorModelsB;
    },
    wisSensorDropdownsC(){

      WisSensor.modules.forEach(item => {
        this.wisSensorModelsC.push(item.model);
      });
      this.currentWisSensorC = this.wisSensorModelsC[2]

      return this.wisSensorModelsC;
    },
    wisSensorDropdownsD(){

      WisSensor.modules.forEach(item => {
        this.wisSensorModelsD.push(item.model);
      });   
      this.currentWisSensorD = this.wisSensorModelsD[3]
      return this.wisSensorModelsD;
    },
    wisIoItems(){
      let newWisIoBoard = [];
      let classStyles = this.removeDuplicates(this.classStyles);

      this.wisIoBoard.forEach(io=>{
        let classStyle =  classStyles.filter((cs)=>cs.pin_name===io.pin_name);    
        
        newWisIoBoard.push({
          func : io.func,
          io_name : io.io_name,
          pin_name : io.pin_name,
          isActive : this.removeDuplicates(classStyle).length? true: false
        })
      })
      
      return this.removeDuplicates(newWisIoBoard);
    }
  },
  methods : {
    removeDuplicates (arr){
      if(arr){
        const filteredArr = Array.from(new Set(arr.map(a => a.func)))
        .map(item => {
          return arr.find(a => a.func === item)
        })

        return filteredArr;
      }
    },
    clearPins(){
      
      this.currentWisBase = null;
      this.currentWisCore = null;
      this.currentWisIo = null;
      this.currentWisSensorA = null;
      this.currentWisSensorB = null;
      this.currentWisSensorC = null;
      this.currentWisSensorD = null;
      this.classStyles = null;

      this.wisIoBoard = []      
      this.wisSensorBoardA = []
      this.wisSensorBoardB = []
      this.wisSensorBoardC = []
      this.wisSensorBoardD = []
    },
    async mapPins (){
      console.log( this.currentWisBase,
      this.currentWisCore,
      this.currentWisIo,
      this.currentWisSensorA,
      this.currentWisSensorB,
      this.currentWisSensorC,
      this.currentWisSensorD,
      this.classStyles)
      let wisCoreObj = WisCore.modules.filter((item)=> item.model === this.currentWisCore);
      let wisIoObj = WisIo.modules.filter((item)=> item.model === this.currentWisIo);
      let wisSensorObjA = WisSensor.modules.filter((item)=>item.model === this.currentWisSensorA);
      let wisSensorObjB = WisSensorB.modules.filter((item)=>item.model === this.currentWisSensorB);
      let wisSensorObjC = WisSensorC.modules.filter((item)=>item.model === this.currentWisSensorC);
      let wisSensorObjD = WisSensorD.modules.filter((item)=>item.model === this.currentWisSensorD);

      let wisCoreObjFiltered = wisCoreObj.length!==0?wisCoreObj[0]:[];
      let wisIoObjFiltered = wisIoObj.length!==0?wisIoObj[0].content.filter((item)=> item.mark !== null):[];
      let wisSensorObjFilteredA = wisSensorObjA.length !== 0 ? wisSensorObjA[0].content.filter((item)=> item.mark !== null) : [];
      let wisSensorObjFilteredB = wisSensorObjB.length !== 0 ? wisSensorObjB[0].content.filter((item)=> item.mark !== null) : [];
      let wisSensorObjFilteredC = wisSensorObjC.length !== 0 ? wisSensorObjC[0].content.filter((item)=> item.mark !== null): [];
      let wisSensorObjFilteredD = wisSensorObjD.length !== 0 ? wisSensorObjD[0].content.filter((item)=> item.mark !== null): [];
            

      this.wisIoBoard = this.removeDuplicates(await this.filterWisIo(wisIoObjFiltered,wisCoreObjFiltered,Pins));

      this.wisSensorBoardA = this.removeDuplicates(await this.filterWisSensor(wisSensorObjFilteredA,wisCoreObjFiltered));
      this.wisSensorBoardB = this.removeDuplicates(await this.filterWisSensor(wisSensorObjFilteredB,wisCoreObjFiltered));
      this.wisSensorBoardC = this.removeDuplicates(await this.filterWisSensor(wisSensorObjFilteredC,wisCoreObjFiltered));
      this.wisSensorBoardD = this.removeDuplicates(await this.filterWisSensor(wisSensorObjFilteredD,wisCoreObjFiltered));

    },

    async filterWisIo(wisIoObjFiltered,wisCoreObjFiltered,Pins){
      let wisIoBoard = [];
      await wisIoObjFiltered.forEach(wisio=>{
        let wisCoreContent = wisCoreObjFiltered.content;
        let wisCoreModel = wisCoreObjFiltered.model;
        wisCoreContent.forEach((wiscore)=>{
          if(wisio.pin_name === wiscore.func) {

            let pins = Pins.wiscorePins;
            let pin = pins.filter((pin)=> pin.model === wisCoreModel);
            let wisCorePins = pin[0].pins;
            let pinName = wisCorePins.filter((pin)=> pin.name === wiscore.pin_name);
            
            wisIoBoard.push({
              func : wisio.func,
              io_name : wisio.pin_name,
              pin_name : pinName.length ? pinName[0].arduino_gpio : wiscore.pin_name
            })
          }
        })
      });

      return wisIoBoard;
    },
    async filterWisSensor(wisSensorObj,wisCoreObj){
      let wisSensorBoard = [];

      if(wisSensorObj){
        await wisSensorObj.forEach(wissensor=>{
          let wisCoreContent = wisCoreObj.content;
          let wisCoreModel = wisCoreObj.model;
          wisCoreContent.forEach((wiscore)=>{
            if(wissensor.pin_name === wiscore.func) {
              let wisIoObj = this.wisIoBoard;
              let classStyle = wisIoObj.filter((cs)=>wissensor.pin_name === cs.io_name);    
              let pins = Pins.wiscorePins;
              let pin = pins.filter((pin)=> pin.model === wisCoreModel);
              let wisCorePins = pin[0].pins;
              let pinName = wisCorePins.filter((pin)=> pin.name === wiscore.pin_name);

              this.classStyles.push(...classStyle)
              wisSensorBoard.push({
                func : wissensor.func,
                io_name : wissensor.pin_name,
                pin_name : pinName.length ? pinName[0].arduino_gpio : wiscore.pin_name,
                isActive : classStyle.length? true:false,
              })
            }
          })
        });
      }
      
      return wisSensorBoard;
    }
  },
  created(){
   
  }
}
</script>
